#include<cstdio>
#include<algorithm>
#include<cstring>
#include<cstdlib>
#include<cmath>
#include<iostream>
#include<vector>
using namespace std;

int a[3];

int main()
{
	int x,y;
	cin>>x>>y;
	a[0]=a[1]=a[2]=y;
	int ans=0,pos=0;
	while (true)
	{
		if (a[0]==x && a[1]==x && a[2]==x) break;
		int s=a[0]+a[1]+a[2]-a[pos];
		a[pos]=min(x,s-1);
		pos++;pos%=3;
		ans++;
	}
	cout<<ans<<endl;
	return 0;
}
