int find_p(int n) {
    int ret = n;
    rep(i, 1, n-1)
        if(a[i] == 0 && a[i + 1] == 1 && ret == n)
            ret = i;
    return ret;
}
void solve() {
    int n; std::cin >> n;
    rep(i, 1, n) std::cin >> a[i];
    std::vector<int> ans;
    if(a[1]) {
        ans.push_back(n + 1);
        rep(i, 1, n) ans.push_back(i);
    } else {
        int f = find_p(n);
        rep(i, 1, f) ans.push_back(i);
        ans.push_back(n + 1);
        rep(i, f+1, n) ans.push_back(i);
    }
    print(ans);
}
