// Problem: C. Need for Pink Slips
// Contest: Codeforces - Codeforces Round #730 (Div. 2)
// URL: https://codeforces.com/contest/1543/problem/C
// Memory Limit: 256 MB
// Time Limit: 1000 ms
// 
// Powered by CP Editor (https://cpeditor.org)

//And in that light,I find deliverance.
#include<bits/stdc++.h>
using namespace std;
#define int long long
inline int read(){
   int s=0,w=1;
   char ch=getchar();
   while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
   while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
   return s*w;
}
double dfs(double a,double b,double c,double v,int s,double p)
{
	double res=0;
	res+=p*c*s;
	if(a>1e-9)
	if(a<v) 
	{
		if(b>1e-9)
		res+=dfs(0,b+a/2.0,c+a/2.0,v,s+1,p*a);
		else res+=p*a*(s+1);
	}
	else 
	{
		if(b>1e-9)
		res+=dfs(a-v,b+v/2.0,c+v/2.0,v,s+1,p*a);
		else 
		res+=dfs(a-v,0,c+v,v,s+1,p*a);
	}
	if(b>1e-9)
	if(b<v) 
	{
		if(a>1e-9)
		res+=dfs(a+b/2.0,0,c+b/2.0,v,s+1,p*b);
		else res+=p*b*(s+1);
	}
	else 
	{
		if(a>1e-9)
		res+=dfs(a+v/2.0,b-v,c+v/2.0,v,s+1,p*b);
		else 
		res+=dfs(0,b-v,c+v,v,s+1,p*b);
	}
	return res;
}
signed main()
{
	for(int T=read();T--;)
	{
		double a,b,c,v;
		double ans=0;
		cin>>a>>b>>c>>v;
		double cur=1;
		printf("%.10lf\n",dfs(a,b,c,v,1,1.0));
	}
	return 0;
}
