pair<ll,int> lcm(int a,int b,int ad) {

	return {(ll)a*b/__gcd(a,b),ad};

}

int main() {

	int a,b;

	scanf("%d %d",&a,&b);

	if(a==b) {

		printf("0");

		return 0;

	}

	pair<ll,int> res=lcm(a,b,0);

	if(a>b) swap(a,b);

	int md=b-a;

	for(int i=1;i*i<=md;i++) {

		if(md%i==0) {

			umin(res,lcm(a+i-a%i,b+i-b%i,i));
			umin(res,lcm(a+md/i-a%(md/i),b+md/i-b%(md/i),md/i));

		}

	}

	if(!res.nd) printf("0");
	else printf("%d",res.nd-a%res.nd);

