#include <bits/stdc++.h>

using namespace std;

int main() {
    int t; cin>>t;
    for (int ti = 0; ti < t; ++ti) {
        int n,m; cin>>n>>m;
        int s = n*m;
        int r = m+1;
        for (int x = 0; x < n; ++x) {
            for (int y = 0; y < m; ++y) {
                cout << r+y << (y == m-1 ? "\n" : " ");
            }
            r += 2*m;
            if (r > s) {
                r = 1;
            }
        }
    }
}
