#include <algorithm>
#include <cstring>
#include <cstdlib>
#include <cstdio>
#define int64 long long
#define N 5010
#define For(i,x,y) for (i=x;i<=y;i++)
using namespace std;
int i,j,k,n,m,an;
int a[N];
inline int64 work(int l,int r,int cen) {
	int i,last=l,mi=a[l];
	int64 s=r-l+1,sum;
	For(i,l,r) mi=min(mi,a[i]);
	sum=mi-cen;
	For(i,l,r) if (a[i]==mi) {
		if (last<i) sum+=work(last,i-1,mi);
		last=i+1;
	}
	if (last<=r) sum+=work(last,r,mi);
	return min(s,sum);
}
int main() {
	scanf("%d",&n);
	For(i,1,n) scanf("%d",&a[i]);
	an=work(1,n,0);
	printf("%I64d\n",an);
	return 0;
}
