#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

#define DEB 1
#define dbg_tab(x,l) if(DEB) { cerr << #x << ":"; for(int i=0;i<l;i++) cerr << " " << x[i]; cerr << endl; }
#define dbg_vec(x) if(DEB) { cerr << #x << ":"; for(auto y:x) cerr << " " << y; cerr << endl; }

const int N = 2e5;
int t[N + 7];
int n;
int wsk[N + 7];

int main() {
    ios_base::sync_with_stdio(0);
    cin >> n;
    for(int i = 1; i <= n; ++i) {
        cin >> t[i];
        wsk[t[i]] = 1;
    }  

    int res = 0;
    for(int i = 1; i <= n; ++i) if(!wsk[i]) res++;
    cout << res << endl;
    return 0;
}
