#include <bits/stdc++.h>
using namespace std;

typedef pair<int, int> pii;
#define fst first
#define snd second
#define mp make_pair

#define rep(i, n) for(int i = 0; i < n; ++i)

#define ri(x) scanf("%d", &x)
#define rii(x,y) scanf("%d%d", &x, &y)
#define riii(x,y,z) scanf("%d%d%d", &x, &y, &z)
#define ria(a, n) rep(_, n) scanf("%d", &a[_])

#define pi(x) printf("%d\n", x)
#define pia(a, n) rep(_, n) printf("%d%c", a[_], _==n-1?'\n': ' ')

#define Ttimes int T; scanf("%d", &T); while(T--)

long long n, k, d1, d2;
bool g(int i, int j) {
    long long a=0, b, c;
    b = a + d1 * i;
    c = b + d2 * j;

    long long m = min(a, b); m = min(m, c);
    a -= m; b -= m; c -= m;

    long long s = a + b + c;
    if((k-s)%3 != 0 || s > k) return false;

    m = max(a, b); m = max(m, c);
    long long r = 3*m - a - b - c;

    long long t = n - k;
    if((t-r)%3 != 0 || r > t) return false;
    return true;
}

int main() {
    Ttimes {
        cin >> n >> k >> d1 >> d2;
        bool f = false;
        for(int i = 0; i < 2; ++i)
            for(int j = 0; j < 2; ++j)
                if(g(i*2-1, j*2-1)) {
                    f = true;
                    break;
                }
        puts(f?"yes":"no");
    }
    return 0;
}
