#include <stdio.h>

int N;
long long V[100005];
long long T[100005];
long long S[100005];
int D[100005];
long long E[100005];
int X[100005];

long long sum(int s, int e) {
	return S[e] - S[s - 1];
}

int main() {
	scanf("%d", &N);
	for(int i = 1; i <= N; i++) scanf("%lld", &V[i]);
	for(int i = 1; i <= N; i++) scanf("%lld", &T[i]);
	for(int i = 1; i <= N; i++) S[i] = S[i - 1] + T[i];

	for(int i = 1; i <= N; i++) {
		int l = i, r = N;
		while(l <= r) {
			int m = (l + r) >> 1;
			if(V[i] < sum(i, m)) r = m - 1;
			else l = m + 1;
		}
		//r
		//printf("%d %d\n", i, r);
		D[i]++; D[l]--;
		E[l] += V[i] - sum(i, r);
	}
	for(int i = 1; i <= N; i++) X[i] = X[i - 1] + D[i];
	for(int i = 1; i <= N; i++) {
		printf("%lld ", X[i] * T[i] + E[i]);
	}

	return 0;
}
