#include <cstdio>
#include <cstring>
#include <algorithm>

using namespace std;

int main()
{
	long long n,m,k,fn;
	scanf("%I64d", &n);
	if (n == 1 || n == 2) {printf("-1\n");return 0;}
	fn = n;
	long long mul = 1;
	for(;n % 2 == 0;n /= 2,mul *= 2);
	if (fn == mul)
	{
		long long base = fn / 4;
		printf("%I64d %I64d\n", fn - base,fn + base);
		return 0;
	}
	m = (n * n + 1) / 2,k = (n * n - 1) / 2;
	if (m > k) swap(m,k);
	printf("%I64d %I64d\n", m * mul, k * mul);
	return 0;
}
