#include <bits/stdc++.h>

#define pb push_back
#define F first
#define S second
#define all(ass) ass.begin(), ass.end()
using namespace std;

typedef long long ll;
typedef string str;
typedef pair<ll, ll> pll;

const ll Mod = 1000000007;
const ll N = 2e5 + 85;
const ll Inf = 1e18;
const ll Log = 69;

int main(){
	ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
	ll t;
	cin >> t;
	for(int i = 0; i < t; i++){
		ll n, k;
		cin >> n >> k;
		vector<ll> A, B;
		ll x;
		for(int i = 0; i < n; i++){
			cin >> x;
			A.pb(x);
		}
		for(int i = 0; i < k; i++){
			cin >> x;
			B.pb(x);
		}
		sort(all(A));
		sort(all(B));
		reverse(all(B));

		ll po = 0;
		ll po2 = n - k;
		ll ans = 0;
		for(auto x : B){
			ans += A[po2];
			if(x == 1) ans += A[po2];
			else ans += A[po];
			po2 ++;
			po += x - 1;
		}
		cout << ans << '\n';
	}
	return 0;
}
