#include<bits/stdc++.h>
using namespace std;
typedef long long int uli;
int main(){
  int n;
  scanf("%d",&n);
  vector<pair<int,int> >a(n);
  multiset<int>all;
  for(int i=0;i<n;i++){
    int ai;
    scanf("%d",&ai);
    a[i]={ai,i};
    all.insert(ai);
  }
  sort(a.begin(),a.end());
  int ans=0;
  for(int i=n-1;i>=0;i--){
    int v=a[i].first;
    int maxi=*all.rbegin();
    if(maxi>v){
      ans++;
      all.erase(all.find(*all.rbegin()));
    }
    else{
      all.erase(all.find(*all.begin()));
    }
  }
  printf("%d\n",ans);
  return 0;
}
