#include <iostream>
#include <cstdio>
#include <vector>
#include <cstring>
#include <algorithm>
#include <set>
#include <unordered_map>
using namespace std;

int n, k, s, t;
int x[1000000];
int c[1000000];
int v[1000000];

int main()
{
    ios_base::sync_with_stdio(false);
    cin >> n >> k >> s >> t;
    for (int i = 1; i <= n; i++)
    {
        cin >> c[i] >> v[i];
    }
    for (int i = 1; i <= k; i++)
    {
        cin >> x[i];
    }

    sort(x + 1, x + k + 1);
    k++;
    x[k] = s;

    int l = 0;
    int r = 1e9 + 1;
    while (r - l > 1)
    {
        int m = l + (r - l) / 2;
        bool can = true;
        int t1 = 0;
        for (int i = 1; i <= k; i++)
        {
            if (x[i] - x[i - 1] > m)
            {
                can = false;
                break;
            }

            t1 += 2 * (x[i] - x[i - 1]) - min(m - (x[i] - x[i - 1]), x[i] - x[i - 1]);
        }

        if (can && t1 <= t)
        {
            r = m;
        } else
        {
            l = m;
        }
    }

    int mn = 1e9 + 1;
    for (int i = 1; i <= n; i++)
    {
        if (v[i] >= r)
        {
            mn = min(mn, c[i]);
        }
    }

    if (mn == 1e9 + 1)
    {
        cout << -1 << endl;
    } else
    {
        cout << mn << endl;
    }
    return 0;
}
