#include <iostream>
#include <cstring>
#include <cstdio>
using namespace std;

int prime[100100];
int a[100100];

main(){
	int i, j, k, step;
	int n;

	memset( prime, 0, sizeof ( prime ) );
	for ( i = 2; i < 100100; i ++ )
		if ( !prime[i] )
			for ( j = i + i; j < 100100; j += i )
				prime[j] = 1;
	prime[1] = prime[2] = 1;
	prime[4] = 0;
	scanf ( "%d", &n );
	for ( i = 0; i < n; i ++ )
		scanf ( "%d", a + i );
	for ( i = 3; i <= n; i ++ )
		if ( n % i == 0 && prime[i] == 0 ){
			step = n / i;
			for ( j = 0; j < step; j ++ ){
				for ( k = j; k < n; k += step )
					if ( a[k] == 0 )
						break;
				if ( k >= n )
					break;
			}
			if ( j != step )
				break;
		}
	if ( i <= n )
		printf( "YES\n" );
	else
		printf( "NO\n" );

	return 0;
}
