#include<cstdio>
#include<cstring>
#include<vector>
#include<algorithm>

using namespace std;

typedef long long LL;

#define LLF "%I64d"

const int MAX_SIZE = 100000;

vector<int> s[MAX_SIZE + 10];
int n, seq[MAX_SIZE + 10];
int m;

inline int abs(int a) { return a < 0 ? -a : a; }

int main(){
	scanf("%d%d", &m, &n);
	for(int i = 0; i < n; i ++)
		scanf("%d", seq + i);
	n = unique(seq, seq + n) - seq;
	LL ans = 0;
	for(int i = 1; i < n; i ++){
		s[seq[i - 1]].push_back(seq[i]);
		s[seq[i]].push_back(seq[i - 1]);
		ans += abs(seq[i] - seq[i - 1]);
	}
	for(int i = 1; i <= m; i ++)
		sort(s[i].begin(), s[i].end());
	LL maxn = 0;
	for(int i = 1; i <= m; i ++){
		LL tot = 0;
		int siz = s[i].size();
		if(siz == 0)
			continue;
		int mid = siz / 2;
		for(int j = 0; j < mid; j ++)
			tot += s[i][j];
		tot = (LL)mid * s[i][mid] - tot;
		for(int j = mid; j < siz; j ++)
			tot += s[i][j];
		tot -= (LL)(siz - mid) * s[i][mid];
		LL o = 0;
		for(int j = 0; j < siz; j ++)
			o += abs(i - s[i][j]);
		tot = o - tot;
		if(tot > maxn)
			maxn = tot;
	}
	printf(LLF "\n", ans - maxn);

	return 0;
}
