#include <bits/stdc++.h>
using namespace std;
#define fo(i,a,b) for(int i=(a);i<(b);i++)
#define MOD 1000000007
#define MP make_pair
#define PB push_back
typedef long long ll;

int N, t[300], f[200200], ans;
int dr[] = {1, -1, 0, 0};
int dc[] = {0, 0, 1, -1};
char s[200200];
bool h[4];

int main () {
	scanf("%d %s", &N, s);
	t['U'] = 0, t['D'] = 1, t['R'] = 2, t['L'] = 3;
	fo(i, 0, N) f[i] = t[(int) s[i]];

	fo(i, 0, N) {
		if (h[f[i] ^ 1]) {
			ans++;
			fo(j, 0, 4) h[j] = 0;
		}
		h[f[i]] = 1;
	}

	printf("%d\n", ans+1);

	return 0;
}
