#include <iostream>
using namespace std;

const int A = 1<<20;
int n, num[A][2];

int main() {
    ios_base::sync_with_stdio(false);
    cin >> n;
    num[0][0] = 1;
    int sum = 0;
    for (int i = 1; i <= n; i++) {
        int a; cin >> a;
        sum ^= a;
        num[sum][i%2]++;
    }
    long long sol = 0;
    for (int s = 0; s < A; s++)
        for (int i = 0; i < 2; i++)
            sol += ((long long)num[s][i])*(num[s][i]-1);
    cout << sol/2 << endl;

    return 0;
}
