#include<bits/stdc++.h>
#define int long long
using namespace std;
inline int read()
{
	int n=0,f=1,ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		n=n*10+ch-'0';
		ch=getchar();
	}
	return n*f;
}
signed main()
{
	int t,k,x,a,ans=0;
	t=read();
	for(int greg=1;greg<=t;greg++)
	{
		k=read();
		x=read();
		a=read();
		ans=0;
		for(int i=1;i<=x+1;i++)
		{
			int now=ans/(k-1)+1;
			ans+=now;
			if(ans>a)break;
		}
		//printf("orz%d\n",ans);
		if(ans<=a)printf("YES\n");
		else printf("NO\n");
	} 
}
