#include <iostream>
#include <cstdio>
#include <vector>
#include <algorithm>

using namespace std;


long long ad = 0;

const int MAXN = 100001;


int main()
{
	int n;
	long long k;
	cin >> n >> k;
	long long c = 0;
	for (int i = 1; i <= n; i++)
	{
		long long a;
		cin >> a;
		if (ad - (long long)a * c * (n - i) < k) cout << i << endl;
		else
		{
			ad += c * a;
			c++;
		}
	}
	return 0;
}
