#include<bits/stdc++.h>
#define ll long long
using namespace std;

int T, a, b;

int calc(int x,int y){
    int res=0;
    for (int i=20;i>=0;--i){
        if (y>>i&1){
            int las=(1<<i)-1;
            if ((y&las)>=(x&las)){
                res|=(x>>i&1)<<i;
            }else{
                res|=1<<i;
                return res;
            }
        }
    }
    return res;
}

int main(){
    for (cin>>T;T--;){
        scanf("%d%d",&a,&b);
        int ans=b-a;
        for (int i=b;i<=b+ans;++i){
            int t=i-b+calc(a,i)-a+1;
            ans=min(ans,t);
        }
        printf("%d\n",ans);
    }
}
