#include <bits/stdc++.h>

using namespace std;

long long firstMultiple(long long b, long long d) {
    long long res = b-b%d;
    if(res < b) {
        res += d;
    }
    return res;
}

bool possible(long long a, long long b, long long c, long long d) {
    return a <= c && b-a <= d-c;
}

void solve() {
    long long a,b,c,d;
    cin >> a >> b >> c >> d;
    if(c == d && a < b) {
        cout << -1 << endl;
        return;
    }
    if(a > 0 && c == 0 ) {
        cout << -1 << endl;
        return;
    }
    long long f = firstMultiple(b,d);
    long long mn = f/d-1, mx = 1000000000LL;
    while(mx-mn > 1) {
        long long med = (mx+mn)/2;
        if(possible(a,b,c*med,d*med)) {
            mx = med;
        } else {
            mn = med;
        }
    }
    cout << d*mx-b << endl;
    return;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    int n;
    cin >> n;
    for(int i=0;i<n;i++) {
        solve();
    }    
}
