#include <cstdio>
#include <cstdlib>
#include <iostream>
#include <algorithm>
#include <vector>
#include <set>

const int maxn = 1e5 + 20;

int n, m;
int a[maxn];
std::vector<int> ans;
std::set<int> S;

int main()
{
#ifndef ONLINE_JUDGE
	freopen("input.txt","r",stdin);
	freopen("output.txt","w",stdout);
#endif

//	std::ios::sync_with_stdio(false);

	scanf("%d%d", &n, &m);
	for(int i = 1; i <= n; i++)
	{
		scanf("%d", &a[i]);
		S.insert(a[i]);
	}

	for(int i = 1; m ; i++)
	{
		if(S.count(i)) continue;

		if(m >= i) ans.push_back(i), m -= i;
		else break;
	}

	printf("%d\n", ans.size());
	for(int i = 0; i < ans.size(); i++) printf("%d ", ans[i]);

#ifndef ONLINE_JUDGE
	fclose(stdin);
	fclose(stdout);
#endif
	return 0;
}
