#include <unordered_set>
#include <memory.h>
using namespace std;

int main() {
  int a, b, q;
  scanf("%d %d %d", &a, &b, &q);
  while (q--) {
    int l0, t, m;
    scanf("%d %d %d", &l0, &t, &m);

    const long long c = 1ll * t * m;
    const long long sl0 = a + 1ll * (l0 - 1) * b;

    int ans = -1;
    long long l = l0, r = 1000000000;
    while (l <= r) {
      const long long m = (l + r) >> 1;
      const long long sm = a + (m - 1) * b;
      const long long w = m - l0 + 1;
      const long long z =
          (w & 1) ? ((sl0 + sm) >> 1) * w : (w >> 1) * (sl0 + sm);

      if (sm <= t && z <= c) {
        l = m + 1;
        ans = m;
      } else {
        r = m - 1;
      }
    }
    printf("%d\n", ans);
  }

  return 0;
}
