using namespace std;

int n,a,b;

int main(){
    //freopen("1.txt","r",stdin);
    //freopen("2.txt","w",stdout);
    scanf("%d%d%d",&n,&a,&b);
    if (b)
    {
       for (int i=0;i<=b;i++) printf("%d ",1<<i);
       for (int i=1;i<=a;i++) printf("%d ",(1<<b)+i);
       for (int i=n-1-a-b;i>=1;--i) printf("1 ");
    }
    else
    if (a)
    {
       if (n==a+1) 
       {
          printf("-1\n");
          return 0;
       }
       printf("1 1 ");
       for (int i=1;i<=a;i++) printf("%d ",1+i);
       for (int i=n-a-2;i>=1;--i) printf("1 ");
    }
    else
       for (int i=1;i<=n;i++) printf("1 ");    
}
