#include <bits/stdc++.h>
using namespace std;

typedef unsigned long long ll;
typedef pair<int, int> pii;
#define sz(x) ((int) (x).size())
#define X first
#define Y second

ll GCD(ll a, ll b) {
	if (b == 0) return a;
	return GCD(b, a % b);
}

int main() {
	ios_base::sync_with_stdio(false); cin.tie(0);
#ifdef _LOCAL
	freopen("input.txt", "r", stdin);
#endif
	ll t, w, b;
	cin >> t >> w >> b;
	ll gcd = GCD(w, b);
	// cerr << gcd << endl;
	double size = log10((double) w) + log10((double) b) - log10((double) gcd);
	ll g;
	if (size > 19.0) g = 0;
	else g = t / (w / gcd * b);
	ll ans = g + g * (min(b, w) - 1);
	if (g != 0) ans += min(min(b, w) - 1, t % (w / gcd * b));
	else ans += min(min(b, w) - 1, t);

	ll p = ans;
	ll q = t;
	gcd = GCD(p, q);
	p /= gcd;
	q /= gcd;
	cout << p << '/' << q << endl;
	return 0;
}

