#define rep(i, a, b) for (int i = a; i < b; ++i)
#define dep(i, a, b) for (int i = a; i > b; --i)
#define N 1005
using namespace std;

int val[N];
int u, v, n, m, ans;

int main()
{
	scanf("%d %d", &n, &m);
	rep(i, 0, n)
	{
		scanf("%d", &val[i]);
	}
	rep(i, 0, m)
	{
		scanf("%d %d", &u, &v);
		ans += min(val[u-1], val[v-1]);
	}
	printf("%d", ans);
}
