#include <bits/stdc++.h>
using namespace std;

const int N = 110000;

int a[N], b[N];

int main() {
#ifndef ONLINE_JUDGE
	freopen("in.txt", "r", stdin);
	freopen("out.txt", "w", stdout);
#endif
	ios_base::sync_with_stdio(0);
	cin.tie(0); cout.tie(0);
	int n, m; cin >> n >> m;
	for (int i = 0; i < n; i++) cin >> a[i];
	for (int i = 0; i < m; i++) cin >> b[i];
	sort(a, a + n);
	sort(b, b + m);
	if (a[n-1] > b[0]) return puts("-1"), 0;
	if (a[n-1] == b[0]) {
		long long ans = 0;
		for (int i = 0; i < n-1; i++) ans += (long long)a[i] * m;
		for (int i = 0; i < m; i++) ans += b[i];
		cout << ans << endl;
		return 0;
	}
	long long ans = 0;
	for (int i = 0; i < n-1; i++) ans += (long long)a[i] * m;
	for (int i = 0; i < m-1; i++) {
		ans += b[i];
	}
	ans -= a[n-2];
	ans += b[m-1];
	ans += a[n-1];
	cout << ans << endl;
	return 0;
}
