#include <bits/stdc++.h>
#define mod 1000000007
#define int long long
using namespace std;
int a[200005],b[200005],n;
inline bool check(int x)
{
	for(int i=1;i<=n;i++) b[i]=0;
	int now=0;
	for(int i=1;i<=x;i++)
		if(a[i]) b[i]=x-now,++now;
	if(now>x) return 0;
	int s=0;
	for(int i=1;i<=n;i++)
		if(a[i]&&a[i]>b[i]) s+=a[i]-b[i];
	if(s+now>x) return 0;
	return 1;
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		cin >> n;
		for(int i=1;i<=n+1;i++) a[i]=0;
		++a[1];
		for(int i=1;i<n;i++)
		{
			int x;
			cin >> x;
			++a[x+1];
		}
		++n;
		sort(a+1,a+n+1);
		reverse(a+1,a+n+1);
		int l=0,r=n,ans=0;
		while(l<=r)
		{
			int mid=(l+r)/2;
			if(check(mid)) ans=mid,r=mid-1;
			else l=mid+1;
		}
		cout << ans << "\n";
	}
	return 0;
}
