#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <cmath>
#include <vector>
#include <map>
#include <algorithm>
using namespace std;
#define MM(a , x) memset(a , x , sizeof(a))
#define sqr(x) ((x) * (x))
#define abs(x) ((x > 0) ? (x) : -(x))
#define REP(i , n) for ((i) = 0; (i) < (n); ++(i))
#define FOR(i , a , b) for ((i) = (a); (i) <= (b); ++(i))
#define FORD(i , a , b) for ((i) = (a); (i) >= (b); --(i))
typedef long long LL;

const int maxn = 100008;

int n , m , tot , ans;
int a[maxn] , cnt[maxn * 2];

void init()
{
	scanf("%d" , &n);
	int i , j;
	FOR (i , 1 , n) scanf("%d" , &a[i]);
	MM(cnt , 0);
	j = 1;
	FOR (i , 1 , n / 2) cnt[maxn + a[i] - j]++ , j++;
	if (n % 2 == 0) j--;
	FOR (i , n / 2 + 1 , n) cnt[maxn + a[i] - j]++ , j--;
}

void work()
{
	ans = n;
	int i , j , k;
	FOR (i , maxn + 0 , maxn + 99999) ans = min(ans , n - cnt[i]);
	printf("%d\n" , ans);
}

int main()
{
	init();
	work();
	return 0;
}
