using namespace std;

#define MOD 10e9+7
double delta=0.0000001;

int main(){
    int a,b;
    cin>>a>>b;
    for(int i=1; i<=a; i++){
        double d=a*a-i*i;
        d=sqrt(d);
        if(abs(d-int(d))<delta){
            for(int e=1; e<=b; e++){
                double d2=b*b-e*e;
                d2=sqrt(d2);
                if(abs(d2-int(d2))<delta){
                    if(e!=i){
                        if(i*e-int(d)*int(d2)==0){
                            cout<<"YES"<<endl;
                            cout<<int(d)<<" "<<i<<endl;
                            cout<<-int(d2)<<" "<<e<<endl;
                            cout<<0<<" "<<0<<endl;
                            return 0;
                        }
                    }
                }
            }
        }
    }
    cout<<"NO"<<endl;
    return 0;
}
