#define Mem(a,b) memset(a,b,sizeof(a))
#define Cpy(a,b) memcpy(a,b,sizeof(b))
//__builtin_popcountll

const int maxn=100+10,oo=1<<30;
int N,A[maxn],F[maxn][3];

int main(int argc, char* argv[])
{
    for (; scanf("%d",&N)!=EOF; )
    {
        For(i,1,N) scanf("%d",&A[i]);
        For(i,1,N) For(j,0,2) F[i][j]=oo;
        F[0][2]=0;
        For(i,1,N)
        {
            For(j,0,2)
                if (F[i-1][j]!=oo)
                {
                    if ((A[i]&1) && j!=0) F[i][0]=min(F[i][0],F[i-1][j]);
                    if ((A[i]&2) && j!=1) F[i][1]=min(F[i][1],F[i-1][j]);
                    F[i][2]=min(F[i][2],F[i-1][j]+1);
                }
        }
        int ans=min(F[N][0],min(F[N][1],F[N][2]));
        printf("%d\n",ans);
    }
    return 0;
}
