#include <bits/stdc++.h>
using namespace std;
#define fr(x, y, z) for(int x=y;x<z;x++)
typedef long long ll;
typedef pair<ll,ll> pll;
typedef pair<pll,ll> ppl;
const int ms=1e6;
ll arr[ms]; 
ll sum=0;
int n,q;
int qry(ll k){
    sum+=k;
    if(arr[n]<=sum)sum=0;
    int l=0, r =n;
    while(l<r-1){
        int m=(l+r)/2;
        if(arr[m]<=sum)l=m;
        else r=m;
    }
    if(arr[r]<=sum)l=r;
    return n-l;
}
int main(){
    scanf("%d%d",&n,&q);
    fr(i,0,n){
        scanf("%lld",&arr[i+1]);
        arr[i+1]+=arr[i];
    }
    fr(i,0,q){
        ll k;
        scanf("%lld",&k);
        printf("%d\n",qry(k));
    }
}
