#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
struct grade{
	ll p, c;
};
grade g[131072];
bool cmp(grade a, grade b){
	return a.c < b.c;
}
int main()
{
	ll n, l, avg, sum = 0;
	cin >> n >> l >> avg;
	avg *= n;
	for(int i = 0 ; i < n ; i++){
		cin >> g[i].p >> g[i].c;
		sum += g[i].p;
	}
	sort(g, g+n, cmp);
	ll re = avg - sum;
	ll c = 0, p = 0;
	while(re > 0){
		if(g[p].p < l){
			int a = min(l - g[p].p, re);
			c += a * g[p].c;
			re -= a;
		}
		p++;
	}
	cout << c << endl;
	return 0;
}
