using namespace std;

typedef long long ll;

void setup(){
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cout.precision(15);
}

ll W, M;

int main(){
    setup();
    cin >> W >> M;

    if(W == 2){ cout << "YES\n"; return 0; }

    for(int i=0; i<=100; i++){
        ll res = M % W; 
        if(res < 0) res += W;

        if(res == 0);
        else if(res == W-1) M++;
        else if(res == 1) M--;
        else { cout << "NO\n"; return 0; }

        M /= W;
    }

    cout << (M ? "NO" : "YES") << endl;
}
