#include<cstdio>
#include<cstring>
#include<cmath>

int A[100000];
int S[100000];

int main()
{
    int i,j,k;
    int cnt = 0;
    bool F;
    scanf("%d", &k);
    for (i = 2 ; i <= k ; ++i)
    {
        if (A[i]) continue;
        F = true;
        for (j = 2 ; j < i ; ++j)
            if ((i % j) == 0) F = false;
        if (F)
        {
            j = i;
            while (j <= k)
            {
                A[j] = true;
                S[++cnt] = j;
                j *= i;
            }
        }
    }
    printf("%d\n", cnt);
    for (i = 1 ; i < cnt ; ++i)
        printf("%d ", S[i]);
    if (cnt) printf("%d\n", S[cnt]);
    return 0;
}
