#include <iostream>
#include <cstdio>
#include <string.h>
#include <string>
#include <algorithm>
#include <cmath>
#include <vector>

using namespace std;
long long n,l,r,ql,qr,a[111111],f[111111],ff[111111];

int main()
{
	cin >> n >> l >> r >> ql >> qr;
	for(int i=1; i<=n; i++) 
	{
		cin >> a[i];
		f[i]=f[i-1]+a[i]*l;
	}
	for(int i=n; i>0; i--) ff[i]=ff[i+1]+a[i]*r;
	long long ans=111111*1ll*111111;
	for(int i=0; i<=n; i++)
	{
		l=i; r=n-i;
		long long mi=f[i]+ff[i+1];
		if (l-r>1) mi+=(l-r-1)*ql;
		else if (r-l>1) mi+=(r-l-1)*qr;
		ans = min(ans, mi);
	}
	cout << ans << endl;
	cin >> n;
	return 0;
}
