#include <cstdio>
#include <cstdlib>
#include <cmath>
#include <algorithm>

struct Item
{
	double x, y, angle;
	static bool Compare(Item a, Item b)
	{
		return a.angle < b.angle;
	}
}a[100005];
int n;

int main()
{
	scanf("%d", &n);
	for(int i(1); i <= n; ++ i)
	{
		scanf("%lf%lf", &a[i].x, &a[i].y);
		a[i].angle = atan2(a[i].y, a[i].x) + M_PI;
	}
	std::sort(a + 1, a + 1 + n, Item::Compare);
	double ans = 0;
	for(int i(2); i <= n; ++ i)
		ans = std::max(ans, a[i].angle - a[i - 1].angle);
	ans = std::max(ans, M_PI * 2 - a[n].angle + a[1].angle);
	printf("%.6lf\n", (M_PI * 2 - ans) / (M_PI * 2) * 360.0);
}
