#include <bits/stdc++.h>

using namespace std;



int main() {
    long long n,x,y,z,a,b;

    scanf("%lld", &n);
    for(x=3*n/2;x>=1;x--) {
        if(n>2*x) break;
        for(y=2*n*x/(2*x-n);y>=1;y--) {
            if(n*(x+y)>2*x*y) break;
            a=n*x*y;
            b=2*x*y-n*y-n*x;
            if(b==0) continue;
            if(a%b!=0) continue;
            z=a/b;
            if(x>=1 && x<=1000000000 && y>=1 && y<=1000000000 && z>=1 && z<=1000000000 && x!=y && x!=z && y!=z) {
                printf("%lld %lld %lld\n", x,y ,z);
                return 0;
            }
        }
    }
    printf("-1\n");

    return 0;
}
