#include<iostream>
#include<vector>
#include<set>
#include<algorithm>
#include<cstring>
#include<random>
#include<ctime>
using namespace std;
const int maxn=100005;
int T,n,k,d,a[2005],p[maxn];
signed main(){
	ios::sync_with_stdio(false);cin.tie(0);
	cin>>T;
	while(T--){
		int ans=0,now=0;
		cin>>n>>k>>d;
		for(int i=1;i<=n;++i){
			cin>>a[i];
			if(a[i]==i)++now;
		}
		for(int i=1;i<=k;++i)cin>>p[i];
		p[0]=p[k];
		for(int i=1;i<=min(2*n+1,d);++i){
			ans=max(ans,now+(d-i)/2);
			int x=p[i%k];
			for(int j=1;j<=x;++j){
				if(a[j]==j)--now;
				a[j]++;
				if(a[j]==j)++now;
			}
		}
		cout<<ans<<'\n';
	}
	return 0;
}
