#include <bits/stdc++.h>
using namespace std;
#define int long long

#ifndef ONLINE_JUDGE
#include "debug.h"
#else
#define deb(...) 4
#endif

int solve(vector<int>v){
	int n=v.size();
	sort(v.begin(),v.end());
	int sm=0;
	int ans=0;
	for(int i=0;i<n;i++){
		ans+=i*v[i]-sm;
		sm+=v[i];
	}
	return ans;
}
int calc(vector<pair<int,int>>v){
	vector<int>x,y;
	for(auto p:v){
		x.push_back(p.first);
		y.push_back(p.second);
	}
	return solve(x)+solve(y);
}
signed main()
{
	ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);

	int n,m;
	cin>>n>>m;
	map<int,vector<pair<int,int>>>mp;
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			int x;
			cin>>x;
			mp[x].push_back({i,j});
		}
	}
	int ans=0;
	for(auto p:mp){
		ans+=calc(p.second);
	}
	cout<<ans;
	return 0;
}
