#include <queue>
#include <map>
using namespace std;

int main()
{
	long long n;
	cin >> n;
	long long i;
	long long div = 1;
	int div_count = 0;
	for(i = 2; i * i <= n; i++)
	{
		while(n % i == 0)
		{
			n /= i;
			div_count++;
			if(div_count <= 2)
			{
				div *= i;
			}
		}
		if(div_count > 2)
		{
			break;
		}
	}
	if(n > 1)
	{
		div_count++;
		if(div_count <= 2)
		{
			div *= n;
		}		
	}
	if(div_count <= 1)
	{
		cout << "1\n0\n";
	}
	else
	{
		if(div_count == 2)
		{
			cout << "2\n";
		}
		else
		{
			cout << "1\n" << div << endl;
		}
	}
	return 0;
}
