#include<iostream>
#include<cstring>
#include<cmath>
#include<cstdio>

using namespace std;

int N;
double A,S,cepat,waktu;

int main() {
	scanf("%d %lf %lf",&N,&A,&S);
	double jawab = 0.0;
	for (int i=1;i<=N;++i) {
		scanf("%lf %lf",&waktu,&cepat);
		if ((cepat*cepat) / (2.0*A) < S) waktu += (cepat/A) + (S-(cepat*cepat) / (2.0*A)) / cepat;
		else waktu += sqrt(2.0*S / A);
		jawab = max(jawab,waktu);
		printf("%.6lf\n",jawab);
	}
	return 0;
}
