#include <iostream>
#include <cstring>
#include <cstdio>
#include <algorithm>
using namespace std;
long long s, x;
long long f[100][2];
long long dfs(int p, int up)
{
	if (p == 60 && up == 0) return 1;
	long long &ans = f[p][up];
	if (ans != -1) return ans;
	ans = 0;
	for (int i = 0; i < 2; ++ i)
	{
		for (int j = 0; j < 2; ++ j)
		{
			if ((i + j + up) % 2 != (s >> p & 1)) continue;
			if ((i ^ j) != (x >> p & 1)) continue;
			ans += dfs(p + 1, (i + j + up) / 2);
		}
	}
	return ans;
}
int main()
{
	memset(f, -1, sizeof(f));
	cin >> s >> x;
	long long ans = dfs(0, 0);
	if (s == x) ans -= 2;
	cout << ans << endl;
}
