#include <iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>

using namespace std;

int a[200];

int main()
{
	int n;
	cin >> n;
	int s = 0;
	int sa = 0;
	for (int i = 0 ;i  <2 * n - 1 ;i++)
	{
		scanf("%d", &a[i]);
		if (a[i] < 0)
		{
			s ++;
			a[i] = -a[i];
		}
		sa = sa + a[i];
	}
	sort(a,a+(2*n-1));
	if (s % 2 == 1 && n % 2 == 0)
	{
	 	sa = sa - a[0] * 2;
	}
	cout << sa << endl;
//	system("pause");
}
