#include<algorithm>
#include<cstdio>
#include<vector>
#include<map>
typedef long long ll;
const int maxn=2e5+5;
int t;
int n;
int a[maxn],fir[maxn],las[maxn];
std::map<int,bool>apr;
signed main(){
	scanf("%d",&t);
	for(int wc=1;wc<=t;wc++){
		apr.clear();
		scanf("%d",&n);
		for(int i=1;i<=n;i++)scanf("%d",a+i),fir[i]=(!apr[a[i]]),apr[a[i]]=true;
		apr.clear(),las[n+1]=0;
		for(int i=n;i;i--)las[i]=las[i+1]+(!apr[a[i]]),apr[a[i]]=true;
		ll ans=0;
		for(int i=1;i<=n;i++)ans+=fir[i]*las[i];
		printf("%lld\n",ans);
	}
	return 0;
}
