#include <iostream>
#include <cstdio>
#include <cstring>
#include <algorithm>
#include <cmath>
#include <map>
#include <queue>
#include <vector>
#include <cstdlib>
#include <functional>
#include <string>
#define N 100010
using namespace std;
int a[N];
map<int,int>vis;
int main()
{
    int n,k;
    scanf("%d%d",&n,&k);
    for(int i=1;i<=n;i++)
     scanf("%d",&a[i]);
    sort(a+1,a+n+1);
    int ans=0;
    for(int i=1;i<=n;i++)
    {
        if(a[i]%k==0&&vis[a[i]/k])continue;
        vis[a[i]]=1;
        ans++;
    }
    printf("%d\n",ans);
    return 0;
}
