using namespace std;

long long a[222222];
long long p[222222], s[222222];

int main() {
    ios::sync_with_stdio(0);
    int n, k, x;
    cin >> n >> k >> x;
    for(int i = 1; i <= n; ++i)
        cin >> a[i];

    p[0] = 0;
    for(int i = 1; i <= n; ++i)
        p[i] = p[i-1] | a[i];
    s[n+1] = 0;
    for(int i = n; i >= 1; --i)
        s[i] = s[i+1] | a[i];


    long long mx = s[1];
    for(int i = 1; i <= n; ++i) {
        long long tmp = a[i];
        for(int i = 0; i < k; ++i)
            tmp *= x;

        mx = max(mx, (tmp | s[i+1]) | p[i-1]);
    }

    cout << mx << "\n";
    return 0;
}
