	                  */
	int last=k;
	while(last!=0)
	{
		for(int i=0;i<last;++i)
		{
			if(w[i][last - 1] != -1 && w[i][last - 1] < n)
			{
				a[cur++] = w[i][last - 1];
				last = i;
				break;
			}
		}
	}
	for(int i=0;i<cur;++i)
	{
		ans+=a[i]*st;
		st*=n;
	}
	cout<<ans;
	return 0;	
