#include<bits/stdc++.h>
using namespace std;

int f[1001][1001][11];
char s1[1011],s2[1011];
int len[1011][1011];
int g[1001][1001][11];

int m,n,q;
int ans = 0;
int main()
{
	scanf("%d%d%d",&m,&n,&q);
	scanf("%s",s1+1);
	scanf("%s",s2+1);

	for(int i=1;i<=m;i++)
		for(int j=1;j<=n;j++)
			if(s1[i]==s2[j])
				len[i][j]=len[i-1][j-1]+1;
			else
				len[i][j]=0;

	for(int i=1;i<=m;i++)
		for(int j=1;j<=n;j++)
			for(int k=1;k<=q;k++)
			{
				int w = len[i][j];
				if(w!=0)
					f[i][j][k]=g[i-w][j-w][k-1]+w;
				else f[i][j][k]=0;
				g[i][j][k]=max(g[i][j][k],g[i-1][j][k]);
				g[i][j][k]=max(g[i][j][k],g[i][j-1][k]);
				g[i][j][k]=max(g[i][j][k],g[i-1][j-1][k]);
				g[i][j][k]=max(g[i][j][k],f[i][j][k]);
				ans=max(ans,g[i][j][k]);
			}
	cout<<ans<<endl;
	return 0;
}
