#include <cstdio>
#include <vector>
#include <algorithm>
#include <map>
#include <set>
#include <cstring>
#include <string>
#include <iostream>
#include <queue>

using namespace std;

int n, X;
int c[100];

int dp[100][10001];

int process(int i, int food){
    if (i == n) return 0;
    if (dp[i][food] != -1)
        return dp[i][food];
    int ans=0, v;
    ans = process(i+1, food);
    if ((n-i)*c[i] <= food) {
        v = 1 + process(i+1, food - (n-i)*c[i]);
        ans = max(ans, v);
    }
    dp[i][food] = ans;
    return ans;
}

int main() {
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
    memset(dp, -1, sizeof(dp));
    scanf("%d%d", &n, &X);
    for(int i=0; i<n; i++)
        scanf("%d", &c[i]);
    printf("%d\n", process(0, X));
    return 0;
}
